
public abstract class AbstractClass {	// 추상 클래스 선언
	private int num;

	public AbstractClass(int num) {
		super();
		this.num = num;
	}

	public AbstractClass() {
		super();
		// TODO Auto-generated constructor stub
	}

	public int getNum() {
		return num;
	}

	public void setNum(int num) {
		this.num = num;
	}
	
	

}
